import React from 'react';
import Todos from './Todos';
import NotToDo from './NotTodo';
class App extends React.Component {
  state = {
    todos: [
      {id:1,content:'Buy some Milk'},
      {id:2, content:'Play clash of clan'}
    ],
    nottodo: [
      {id:5,content:" Not Play game a lot"},
      {id:6,content:" Do not watch Youtube more"}
    ]
  }


  deleteTodo  = (id) => {
    const todos = this.state.todos.filter( todo => {
     return todo.id !== id
    });

    this.setState({
      todos
    })
  }

  deleteNotTodo = (id) => {
    const notTodo = this.state.nottodo.filter( notdo => {
      return notdo.id !== id
    });
    this.setState({
      notTodo
    })
  }
  render() {
    return(
      <div className="todo-app container">
        <h1 className="center blue-text">Todo's</h1>
        <Todos todos={this.state.todos} deleteTodo={this.deleteTodo}/>
        <NotToDo nottodo={this.state.nottodo} deleteNotTodo={this.deleteNotTodo} />
      </div>
    )
  } 
}

export default App;
